import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import AddToCartButton from "../components/AddToCart";
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import { toast } from "react-toastify";

// The Menu component now receives the menuItems and searchQuery props
const Menu = ({ isAdmin, searchQuery, menuItems }) => {
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const navigate = useNavigate();

  // Filter items based on the search query first
  const searchedItems = searchQuery.length > 0
    ? menuItems.filter(item =>
        item.name.toLowerCase().includes(searchQuery.toLowerCase())
      )
    : menuItems;

  // Then filter by category
  const filteredItems = selectedCategory === "All"
    ? searchedItems
    : searchedItems.filter(item => item.category === selectedCategory);

  const categories = ["All", ...new Set(menuItems.map(item => item.category))];

  const handleDelete = async (itemId) => {
    setItemToDelete(itemId);
    setIsModalOpen(true);
  };

  const confirmDelete = async () => {
    try {
      await axios.delete(`http://localhost:5000/api/menu/${itemToDelete}`);
      toast.success("Item deleted successfully!");
      // No need to manually update state, App.js will refetch and pass new props
    } catch (error) {
      toast.error("Error deleting item.");
      console.error("Error deleting item:", error);
    } finally {
      setIsModalOpen(false);
      setItemToDelete(null);
    }
  };

  const cancelDelete = () => {
    setIsModalOpen(false);
    setItemToDelete(null);
  };

  const handleUpdate = (item) => {
    // You can handle the update logic here, e.g., navigate to an update form
    navigate('/admin/create-menu', { state: { itemToUpdate: item } });
  };

  return (
    <section id="menu" className="p-[80px]">
      <div className="text-center">
        <h2 className="text-[2.5rem] font-bold text-[#333]">
          Our Menu
        </h2>
        <p className="text-[#555] max-w-[600px] mx-auto mt-4">
          Discover our wide range of delicious food and drinks, crafted with the freshest ingredients to satisfy your cravings.
        </p>
      </div>

      {/* Category Tabs */}
      <div className="category-tabs flex justify-center gap-4 mt-8 flex-wrap">
        {categories.map((category) => (
          <button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className={`px-6 py-2 rounded-full font-semibold transition-all duration-300 ${
              selectedCategory === category
                ? "bg-orange-500 text-white shadow-lg"
                : "bg-gray-200 text-gray-700 hover:bg-gray-300"
            }`}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Menu Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mt-12">
        {filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <motion.div
              key={item._id}
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300 overflow-hidden"
            >
              {/* Image */}
              <div className="relative">
                <img
                  src={item.image}
                  alt={item.name}
                  className="w-full h-48 object-cover"
                />
              </div>
              {/* Content */}
              <div className="p-4">
                {/* Title and Rating */}
                <div className="flex items-center justify-between">
                  <h3 className="text-[1.2rem] font-bold text-[#333]">{item.name}</h3>
                  <div className="rating text-orange-400">
                    <span className="text-[12px] font-bold">{item.rating}</span>
                    <i className="fas fa-star text-[12px] ml-1"></i>
                  </div>
                </div>

                {/* Description */}
                <p className="text-[14px] text-[#555] leading-[1.4] mt-2">
                  {item.description}
                </p>

                {/* Price */}
                <div className="price mt-3 font-bold">
                  ${Number(item.price ?? 0).toFixed(2)}
                </div>
              </div>

              {/* Conditional rendering of buttons based on isAdmin prop */}
              {isAdmin ? (
                <div className="flex justify-between p-3 gap-3">
                  <button
                    onClick={() => handleUpdate(item)}
                    className="flex-1 bg-blue-500 text-white rounded-md py-2 px-4 transition-colors hover:bg-blue-600"
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(item._id)}
                    className="flex-1 bg-red-500 text-white rounded-md py-2 px-4 transition-colors hover:bg-red-600"
                  >
                    Delete
                  </button>
                </div>
              ) : (
                <AddToCartButton item={item} />
              )}
            </motion.div>
          ))
        ) : (
          <p className="text-center text-gray-500 col-span-full">No menu items to display.</p>
        )}
      </div>

      {/* Custom Confirmation Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex justify-center items-center">
          <div className="bg-white p-6 rounded-lg shadow-xl text-center">
            <h3 className="text-xl font-bold mb-4">Confirm Deletion</h3>
            <p className="text-gray-700 mb-6">Are you sure you want to delete this item?</p>
            <div className="flex justify-center gap-4">
              <button
                onClick={confirmDelete}
                className="bg-red-500 text-white py-2 px-4 rounded-md hover:bg-red-600 transition-colors"
              >
                Yes, Delete
              </button>
              <button
                onClick={cancelDelete}
                className="bg-gray-300 text-gray-800 py-2 px-4 rounded-md hover:bg-gray-400 transition-colors"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </section>
  );
};

export default Menu;
