// src/pages/Menu.js

// ... (other code above) ...

const Menu = ({ isAdmin, searchQuery, menuItems }) => {
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const navigate = useNavigate();

  // New function to handle the "Update" button click
  const handleUpdate = (item) => {
    navigate(`/admin/menu/${item._id}/edit`, { state: { itemToUpdate: item } });
  };

  const handleDelete = (item) => {
    setItemToDelete(item);
    setIsModalOpen(true);
  };

  const confirmDelete = async () => {
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${JSON.parse(localStorage.getItem("userInfo")).token}`,
        },
      };
      await axios.delete(`http://localhost:5000/api/menu/${itemToDelete._id}`, config);
      toast.success("Item deleted successfully!");
      // Reload the page to reflect the deletion
      window.location.reload();
    } catch (error) {
      toast.error("Error deleting item.");
      console.error("Deletion error:", error);
    } finally {
      setIsModalOpen(false);
      setItemToDelete(null);
    }
  };

  const cancelDelete = () => {
    setIsModalOpen(false);
    setItemToDelete(null);
  };

  const filteredItems = useMemo(() => {
    const searchedItems =
      searchQuery.length > 0
        ? menuItems.filter((item) =>
            item.name.toLowerCase().includes(searchQuery.toLowerCase())
          )
        : menuItems;

    return selectedCategory === "All"
      ? searchedItems
      : searchedItems.filter(
          (item) => item.category?.toLowerCase() === selectedCategory.toLowerCase()
        );
  }, [menuItems, searchQuery, selectedCategory]);

  const categories = [
    {
      name: "All",
      img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-foods-restaurant-menu-logo-design-png-image_10201389.png",
    },
    {
      name: "Pizza",
      img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-foods-restaurant-menu-logo-design-png-image_10201389.png",
    },
    {
      name: "Burger",
      img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-foods-restaurant-menu-logo-design-png-image_10201389.png",
    },
    {
      name: "Dessert",
      img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-foods-restaurant-menu-logo-design-png-image_10201389.png",
    },
    {
      name: "Cake",
      img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-foods-restaurant-menu-logo-design-png-image_10201389.png",
    },
    {
      name: "Biriyani",
      img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-foods-restaurant-menu-logo-design-png-image_10201389.png",
    },
    {
      name: "Beverage",
      img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-foods-restaurant-menu-logo-design-png-image_10201389.png",
    },
  ];

  const categoryImages = categories.reduce((acc, cat) => {
    acc[cat.name] = cat.img;
    return acc;
  }, {});

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Category filters */}
      <div className="flex justify-center flex-wrap gap-4 mb-8">
        {categories.map((category) => (
          <motion.button
            key={category.name}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => setSelectedCategory(category.name)}
            className={`py-2 px-6 rounded-full text-sm font-semibold transition-colors duration-200 ${
              selectedCategory === category.name
                ? "bg-orange-600 text-white"
                : "bg-gray-200 text-gray-700 hover:bg-orange-500 hover:text-white"
            }`}
          >
            {category.name}
          </motion.button>
        ))}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <motion.div
              key={item._id}
              className="bg-white rounded-xl shadow-lg overflow-hidden flex flex-col items-center p-4"
              whileHover={{ scale: 1.05 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              {/* Add an image for the category */}
              <div className="flex items-center space-x-2 mb-2">
                <img
                  src={categoryImages[item.category] || categories[0].img}
                  alt={item.category}
                  className="w-8 h-8 rounded-full object-cover"
                />
                <h4 className="text-sm font-semibold text-gray-600">
                  {item.category}
                </h4>
              </div>
              <img
                src={item.image}
                alt={item.name}
                className="w-full h-48 object-cover rounded-md mb-4"
              />
              <div className="text-center w-full">
                <h3 className="text-xl font-bold text-gray-800 mb-1">
                  {item.name}
                </h3>
                <p className="text-sm text-gray-600 mb-2">
                  {item.description}
                </p>
                <div className="flex items-center justify-center mb-2">
                  <span className="text-lg font-bold text-orange-600">
                    ${item.price.toFixed(2)}
                  </span>
                </div>
              </div>
              {/* Conditional rendering for admin actions */}
              {isAdmin ? (
                <div className="flex justify-center w-full gap-2">
                  <button
                    onClick={() => handleUpdate(item)}
                    className="bg-green-500 text-white py-2 px-4 rounded-md transition-colors hover:bg-green-600 w-1/2"
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(item)}
                    className="bg-red-500 text-white py-2 px-4 rounded-md transition-colors hover:bg-red-600 w-1/2"
                  >
                    Delete
                  </button>
                </div>
              ) : (
                <AddToCartButton item={item} />
              )}
            </motion.div>
          ))
        ) : (
          <p className="text-center text-gray-500 col-span-full">
            No menu items to display.
          </p>
        )}
      </div>

      {/* Custom Confirmation Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl text-center">
            <h3 className="text-xl font-bold mb-4">Confirm Deletion</h3>
            <p className="text-gray-700 mb-6">
              Are you sure you want to delete this item?
            </p>
            <div className="flex justify-center gap-4">
              <button
                onClick={confirmDelete}
                className="bg-red-500 text-white py-2 px-4 rounded-md hover:bg-red-600 transition-colors"
              >
                Yes, Delete
              </button>
              <button
                onClick={cancelDelete}
                className="bg-gray-300 text-gray-800 py-2 px-4 rounded-md hover:bg-gray-400 transition-colors"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
