import React, { useEffect, useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { toast } from "react-toastify";
import { useLocation, useNavigate } from "react-router-dom";

// Receive isAdmin as a prop
const Orders = ({ isAdmin }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const itemToUpdate = location.state?.itemToUpdate;

  const [formData, setFormData] = useState({
    foodName: "",
    imageURL: "",
    rating: "",
    reviews: "",
    description: "",
    price: "",
    category: "",
  });

  // Effect to pre-fill the form if an item is being updated
  useEffect(() => {
    if (itemToUpdate) {
      setFormData({
        foodName: itemToUpdate.name || "",
        imageURL: itemToUpdate.image || "",
        rating: itemToUpdate.ratings || "",
        reviews: itemToUpdate.reviews || "",
        description: itemToUpdate.description || "",
        price: itemToUpdate.price || "",
        category: itemToUpdate.category || "",
      });
    }
  }, [itemToUpdate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (itemToUpdate) {
        // Handle Update (PUT request)
        await axios.put(`http://localhost:5000/api/menu/${itemToUpdate._id}`, {
          name: formData.foodName,
          image: formData.imageURL,
          ratings: formData.rating,
          reviews: formData.reviews,
          description: formData.description,
          price: formData.price,
          category: formData.category,
        });
        toast.success("Menu item updated successfully!");
      } else {
        // Handle Create (POST request)
        await axios.post("http://localhost:5000/api/menu", {
          name: formData.foodName,
          image: formData.imageURL,
          ratings: formData.rating,
          reviews: formData.reviews,
          description: formData.description,
          price: formData.price,
          category: formData.category,
        });
        toast.success("Menu item created successfully!");
      }

      // Clear the form after submission
      setFormData({
        foodName: "",
        imageURL: "",
        rating: "",
        reviews: "",
        description: "",
        price: "",
        category: "",
      });
      navigate('/menu'); // Redirect to the menu page after submission
    } catch (err) {
      console.error("Error submitting form:", err);
      toast.error("Failed to save menu item.");
    }
  };

  if (isAdmin) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-[#fff3eb] p-8">
        <div className="bg-white shadow-lg rounded-2xl p-8 w-full max-w-md">
          <h2 className="text-3xl font-bold text-center mb-6 text-orange-600">
            {itemToUpdate ? "Update Menu Item" : "Create Menu"}
          </h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-gray-700 font-medium">Food Name</label>
              <input
                type="text"
                name="foodName"
                value={formData.foodName}
                onChange={handleChange}
                placeholder="e.g., Chicken Biryani"
                className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 font-medium">Image URL</label>
              <input
                type="text"
                name="imageURL"
                value={formData.imageURL}
                onChange={handleChange}
                placeholder="e.g., https://example.com/image.jpg"
                className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>
            <div>
              <label className="block text-gray-700 font-medium">Category</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
                required
              >
                <option value="">Select a Category</option>
                <option value="Burger">Burger</option>
                <option value="Pizza">Pizza</option>
                <option value="Cake">Cake</option>
                <option value="Dessert">Dessert</option>
                <option value="Biriyani">Biriyani</option>
                <option value="Beverage">Beverage</option>
              </select>
            </div>
            <div>
              <label className="block text-gray-700 font-medium">Rating (1-5)</label>
              <input
                type="number"
                name="rating"
                value={formData.rating}
                onChange={handleChange}
                placeholder="e.g., 4.5"
                step="0.1"
                min="0"
                max="5"
                className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>
            <div>
              <label className="block text-gray-700 font-medium">Reviews</label>
              <input
                type="number"
                name="reviews"
                value={formData.reviews}
                onChange={handleChange}
                placeholder="e.g., 120"
                className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>
            <div>
              <label className="block text-gray-700 font-medium">Description</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
                placeholder="A short description of the item."
                className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
                rows="4"
              ></textarea>
            </div>
            <div>
              <label className="block text-gray-700 font-medium">Price ($)</label>
              <input
                type="number"
                name="price"
                value={formData.price}
                onChange={handleChange}
                placeholder="e.g., 9.99"
                step="0.01"
                className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
                required
              />
            </div>
            <button
              type="submit"
              className="w-full bg-orange-600 text-white font-semibold py-3 px-6 rounded-md transition-colors hover:bg-orange-700"
            >
              {itemToUpdate ? "Update Item" : "Create Menu Item"}
            </button>
          </form>
        </div>
      </div>
    );
  } else {
    // Original Orders page content for non-admin users
    // You have the code for this part, so just ensure it's here
    // ...
  }
};

export default Orders;
