import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useLocation, useNavigate } from "react-router-dom";

const AdminMenuForm = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const itemToUpdate = location.state?.itemToUpdate;

  const [formData, setFormData] = useState({
    name: "",
    image: "",
    ratings: "",
    reviews: "",
    description: "",
    price: "",
    category: "",
  });

  useEffect(() => {
    if (itemToUpdate) {
      setFormData({
        name: itemToUpdate.name || "",
        image: itemToUpdate.image || "",
        ratings: itemToUpdate.ratings || "",
        reviews: itemToUpdate.reviews || "",
        description: itemToUpdate.description || "",
        price: itemToUpdate.price || "",
        category: itemToUpdate.category || "",
      });
    }
  }, [itemToUpdate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        name: formData.name,
        image: formData.image,
        ratings: Number(formData.ratings),
        reviews: Number(formData.reviews),
        description: formData.description,
        price: Number(formData.price),
        category: formData.category,
      };

      if (itemToUpdate) {
        // Update existing item
        await axios.put(`http://localhost:5000/api/menu/${itemToUpdate._id}`, payload);
        toast.success("Menu item updated successfully!");
      } else {
        // Create new item
        await axios.post("http://localhost:5000/api/menu", payload);
        toast.success("Menu item added successfully!");
      }
      navigate("/menu"); // Redirect to the menu page after success
    } catch (err) {
      console.error("Error submitting menu item:", err);
      toast.error("Failed to save menu item.");
    }
  };

  return (
    <div className="flex justify-center items-start min-h-screen bg-[#fff3eb] py-10">
      <div className="w-full max-w-2xl bg-white p-8 rounded-lg shadow-xl border border-gray-200">
        <h1 className="text-3xl font-bold text-center text-[#e96610] mb-8">
          {itemToUpdate ? "Update Menu Item" : "Create New Menu Item"}
        </h1>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="form-group">
            <label htmlFor="name" className="block text-gray-700 font-semibold mb-2">
              Food Name
            </label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="e.g., Delicious Burger"
              required
              className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-[#e96610] transition-shadow"
            />
          </div>

          <div className="form-group">
            <label htmlFor="image" className="block text-gray-700 font-semibold mb-2">
              Image URL
            </label>
            <input
              type="text"
              id="image"
              name="image"
              value={formData.image}
              onChange={handleChange}
              placeholder="e.g., https://example.com/image.jpg"
              className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-[#e96610] transition-shadow"
            />
          </div>

          <div className="form-group">
            <label htmlFor="description" className="block text-gray-700 font-semibold mb-2">
              Description
            </label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="3"
              placeholder="A brief description of the food item..."
              className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-[#e96610] transition-shadow"
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="form-group">
              <label htmlFor="price" className="block text-gray-700 font-semibold mb-2">
                Price
              </label>
              <input
                type="number"
                id="price"
                name="price"
                value={formData.price}
                onChange={handleChange}
                placeholder="e.g., 9.99"
                step="0.01"
                required
                className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-[#e96610] transition-shadow"
              />
            </div>
            <div className="form-group">
              <label htmlFor="category" className="block text-gray-700 font-semibold mb-2">
                Category
              </label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
                className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-[#e96610] transition-shadow bg-white"
              >
                <option value="">Select a category</option>
                <option value="Burger">Burger</option>
                <option value="Pizza">Pizza</option>
                <option value="Cake">Cake</option>
                <option value="Dessert">Dessert</option>
                <option value="Biriyani">Biriyani</option>
                <option value="Beverage">Beverage</option>
              </select>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="form-group">
              <label htmlFor="ratings" className="block text-gray-700 font-semibold mb-2">
                Rating
              </label>
              <input
                type="number"
                id="ratings"
                name="ratings"
                value={formData.ratings}
                onChange={handleChange}
                placeholder="e.g., 4.5"
                step="0.1"
                min="0"
                max="5"
                className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-[#e96610] transition-shadow"
              />
            </div>
            <div className="form-group">
              <label htmlFor="reviews" className="block text-gray-700 font-semibold mb-2">
                Number of Reviews
              </label>
              <input
                type="number"
                id="reviews"
                name="reviews"
                value={formData.reviews}
                onChange={handleChange}
                placeholder="e.g., 120"
                min="0"
                className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-[#e96610] transition-shadow"
              />
            </div>
          </div>

          <div className="flex justify-center mt-8">
            <button
              type="submit"
              className="bg-[#e96610] text-white font-bold py-3 px-8 rounded-full shadow-lg hover:bg-[#ff8c00] transition-colors duration-300"
            >
              {itemToUpdate ? "Update Item" : "Create Item"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdminMenuForm;
