import React, { useEffect, useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTruckFast } from "@fortawesome/free-solid-svg-icons";

// Admin-specific orders component
const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/orders");
        setOrders(res.data);
      } catch (err) {
        console.error("Error fetching orders:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, []);

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      await axios.patch(`http://localhost:5000/api/orders/${orderId}`, {
        status: newStatus,
      });
      // Update the local state to reflect the change
      setOrders(orders.map(order =>
        order._id === orderId ? { ...order, status: newStatus } : order
      ));
    } catch (err) {
      console.error("Error updating order status:", err);
    }
  };

  const formatOrderTime = (createdAt) => {
    const date = new Date(createdAt);
    return date.toLocaleString();
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-[calc(100vh-150px)]">
        <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-orange-500"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4 md:p-8 min-h-[calc(100vh-150px)]">
      <motion.h1
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5 }}
        className="text-4xl font-extrabold text-center text-orange-600 mb-8"
      >
        Customer Orders
      </motion.h1>
      {orders.length === 0 ? (
        <p className="text-center text-gray-500 text-lg mt-10">No customer orders yet.</p>
      ) : (
        <div className="space-y-6">
          {orders.map((order) => (
            <motion.div
              key={order._id}
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.5, delay: 0.1 }}
              className="bg-white rounded-xl shadow-lg p-6 border border-gray-200"
            >
              <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-4">
                <div className="flex-1">
                  <h2 className="text-xl font-bold text-gray-800">Order ID: {order._id}</h2>
                  <p className="text-sm text-gray-500">
                    <span className="font-semibold">Ordered by:</span> {order.userName}
                  </p>
                  <p className="text-sm text-gray-500">
                    <span className="font-semibold">Address:</span> {order.userAddress}
                  </p>
                  <p className="text-sm text-gray-500">
                    <span className="font-semibold">Order Time:</span> {formatOrderTime(order.createdAt)}
                  </p>
                </div>
                <div className="mt-4 md:mt-0 flex items-center">
                  <label className="inline-flex items-center cursor-pointer">
                    <input
                      type="checkbox"
                      className="form-checkbox h-5 w-5 text-orange-600 transition duration-150 ease-in-out"
                      checked={order.status === 'Sent to Restaurant'}
                      onChange={(e) =>
                        handleStatusChange(
                          order._id,
                          e.target.checked ? 'Sent to Restaurant' : 'Pending'
                        )
                      }
                    />
                    <span className="ml-2 text-gray-700 font-semibold">
                      Send to Restaurant
                    </span>
                  </label>
                </div>
              </div>

              <div className="border-t pt-4 mt-4">
                <p className="text-lg font-bold text-gray-800 mb-2">Order Details:</p>
                <div className="space-y-3">
                  {order.items.map((item) => (
                    <div key={item.itemId} className="flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        <img
                          src={item.image}
                          alt={item.name}
                          className="w-12 h-12 object-cover rounded-lg"
                        />
                        <p className="font-semibold text-gray-800">
                          {item.name} <span className="text-gray-500">x{item.quantity}</span>
                        </p>
                      </div>
                      <p className="font-bold text-gray-800">${(item.price * item.quantity).toFixed(2)}</p>
                    </div>
                  ))}
                </div>
              </div>
              
              <div className="mt-6 pt-4 border-t flex justify-between items-center">
                <p className="text-2xl font-bold">
                  Total Price: <span className="text-orange-500">${Number(order.totalPrice).toFixed(2)}</span>
                </p>
                <span
                  className={`py-1 px-3 rounded-full text-sm font-medium ${
                    order.status === 'Sent to Restaurant' ? "bg-green-100 text-green-700" : "bg-yellow-100 text-yellow-700"
                  }`}
                >
                  {order.status}
                </span>
              </div>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
};

// Original About component content
const AboutContent = () => {
  return (
    <div>
      {/* ABOUT */}
      <section
        id="about"
        className="about grid md:grid-cols-[1fr_480px] gap-8 px-[50px] pt-[0px] pb-[40px] items-center mt-[28px]"
      >
        {/* left card */}
        <div className="about-card rounded-[24px] p-[50px] bg-[linear-gradient(180deg,#fff3eb,transparent)] shadow-[0_8px_30px_rgba(236,206,146,0.884)] border-[2px] border-[rgba(236,206,146,0.884)] backdrop-blur-[6px]">
          <h2 className="text-[40px] font-bold leading-[1.02] mb-3 text-[#333]">
            We deliver joy (and food) — on time, every time.
          </h2>
          <p className="text-[16px] text-black max-w-[60%] leading-[1.6] mb-6">
            SwadSeva was founded to make mealtime moments easier. From quick solo
            lunches to weekend feasts, our mission is to connect delicious local
            kitchens to hungry humans with speed, transparency and a little dash of
            delight.
          </p>

          <div className="stats flex gap-3 mt-5">
            <div className="stat bg-white/30 rounded-xl p-[24px] backdrop-blur-md">
              <div className="flex items-center gap-2 mb-2">
                <i className="fa-solid fa-truck-fast text-[#ff5722]"></i>
                <h3 className="text-xl font-bold">100+</h3>
              </div>
              <p className="text-sm text-gray-700">Deliveries Daily</p>
            </div>
            <div className="stat bg-white/30 rounded-xl p-[24px] backdrop-blur-md">
              <div className="flex items-center gap-2 mb-2">
                <i className="fa-solid fa-star text-[#ff5722]"></i>
                <h3 className="text-xl font-bold">4.8</h3>
              </div>
              <p className="text-sm text-gray-700">Average Rating</p>
            </div>
            <div className="stat bg-white/30 rounded-xl p-[24px] backdrop-blur-md">
              <div className="flex items-center gap-2 mb-2">
                <i className="fa-solid fa-user-friends text-[#ff5722]"></i>
                <h3 className="text-xl font-bold">50+</h3>
              </div>
              <p className="text-sm text-gray-700">Partner Restaurants</p>
            </div>
          </div>
        </div>

        {/* right image */}
        <div className="about-image rounded-[24px] overflow-hidden shadow-[0_8px_30px_rgba(0,0,0,0.1)]">
          <img
            src="https://images.unsplash.com/photo-1543360216-95093125d0c8?ixid=M3wxMjA3fDB8MXxzZWFyY2h8MTd8fGZvb2QlMjBkZWxpdmVyeSUyMHJpZGVyfGVufDB8fHx8MTY4MTM5NzI3NXww&ixlib=rb-4.0.3"
            alt="Delivery rider on a bike"
            className="w-full h-full object-cover rounded-[24px] transition-transform duration-500 ease-in-out hover:scale-105"
          />
        </div>
      </section>

      {/* TEAM SECTION */}
      <section id="team" className="team text-center py-20 px-8">
        <h2 className="text-[32px] font-bold mb-4">Our Team</h2>
        <p className="text-[#555] max-w-[600px] mx-auto mb-12">
          Meet the minds behind the magic. We're a small but mighty team,
          dedicated to making your next meal an unforgettable experience.
        </p>
        <div className="team-members grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
          <div className="member bg-white rounded-[20px] p-[30px_20px] shadow-[0_8px_24px_rgba(0,0,0,0.08)] hover:translate-y-[-10px] transition">
            <img
              src="https://images.unsplash.com/photo-1570295999919-56ceb8ecca2e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MXxzZWFyY2h8MjZ8fGhlYWRzaG90JTIwbWFsZXxlbnwwfHwwfHx8MA%3D%3D&auto=format&fit=crop&w=800&q=60"
              alt="Alex"
              className="w-[120px] h-[120px] rounded-full object-cover mb-4 mx-auto shadow-[0_6px_20px_rgba(255,87,34,0.3)]"
            />
            <h3 className="text-[20px] font-bold mb-1">Alex</h3>
            <p className="role text-[#ff5722] font-semibold mb-2">Founder & CEO</p>
            <p className="bio text-[#555] text-[14px]">
              The visionary behind SwadSeva, he's obsessed with good food and
              even better delivery.
            </p>
          </div>
          <div className="member bg-white rounded-[20px] p-[30px_20px] shadow-[0_8px_24px_rgba(0,0,0,0.08)] hover:translate-y-[-10px] transition">
            <img
              src="https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MXxzZWFyY2h8MjB8fGhlYWRzaG90JTIwd29tYW58ZW58MHx8MHx8fDA%3D&auto=format&fit=crop&w=800&q=60"
              alt="Maria"
              className="w-[120px] h-[120px] rounded-full object-cover mb-4 mx-auto shadow-[0_6px_20px_rgba(255,87,34,0.3)]"
            />
            <h3 className="text-[20px] font-bold mb-1">Maria</h3>
            <p className="role text-[#ff5722] font-semibold mb-2">CTO</p>
            <p className="bio text-[#555] text-[14px]">
              A tech wizard who built our super-fast platform from the ground up.
              She loves a good bug hunt.
            </p>
          </div>
          <div className="member bg-white rounded-[20px] p-[30px_20px] shadow-[0_8px_24px_rgba(0,0,0,0.08)] hover:translate-y-[-10px] transition">
            <img
              src="https://images.unsplash.com/photo-1522075469751-cf8740e68e42?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MXxzZWFyY2h8NXx8aGVhZHNob3QlMjBtYWxlfGVufDB8fDB8fHww&auto=format&fit=crop&w=800&q=60"
              alt="Olivia"
              className="w-[120px] h-[120px] rounded-full object-cover mb-4 mx-auto shadow-[0_6px_20px_rgba(255,87,34,0.3)]"
            />
            <h3 className="text-[20px] font-bold mb-1">Olivia</h3>
            <p className="role text-[#ff5722] font-semibold mb-2">Head of Ops</p>
            <p className="bio text-[#555] text-[14px]">
              Builds routing systems and driver-first tools that actually make
              deliveries faster.
            </p>
          </div>
        </div>
      </section>
    </div>
  );
};

// Main component that decides what to render
export default function About({ isAdmin }) {
  return isAdmin ? <AdminOrders /> : <AboutContent />;
}
