import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { toast } from "react-toastify";
import axios from "axios";

// This component should receive isAdmin as a prop from App.js
const Header = ({ user, onLogout, isAdmin }) => {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [menuItems, setMenuItems] = useState([]); // State to hold all menu items
  const navigate = useNavigate();

  // Fetch all menu items once when the component mounts
  useEffect(() => {
    const fetchMenuItems = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/menu");
        setMenuItems(response.data);
      } catch (error) {
        console.error("Error fetching menu items for search:", error);
      }
    };
    fetchMenuItems();
  }, []);

  const handleSearchChange = (e) => {
    const query = e.target.value;
    setSearchQuery(query);

    if (query.length > 0) {
      // Filter menu items based on the search query (case-insensitive)
      const filtered = menuItems.filter(item =>
        item.name.toLowerCase().includes(query.toLowerCase())
      );
      setSearchResults(filtered);
    } else {
      setSearchResults([]); // Clear results if the query is empty
    }
  };

  const handleResultClick = (itemId) => {
    setSearchResults([]); // Clear search results window
    setSearchQuery(""); // Clear the search input
    // Navigate to the menu page with the item ID in the URL as a query parameter
    navigate(`/menu?itemId=${itemId}`);
  };

  const role = user?.role || "user";

  return (
    <header className="header bg-white shadow-md py-4 px-8 sticky top-0 z-50">
      <nav className="flex items-center justify-between">
        {/* ... (existing logo and nav links) ... */}
        <div className="relative">
          <form className="relative flex items-center bg-white rounded-full">
            <i className="fas fa-search absolute left-3 text-gray-400"></i>
            <input
              type="text"
              placeholder="Search food..."
              value={searchQuery}
              onChange={handleSearchChange}
              className="pl-10 pr-4 py-2 rounded-full border border-gray-300 focus:outline-none focus:ring-2 focus:ring-orange-500"
            />
          </form>

          {/* Search Results Window */}
          {searchResults.length > 0 && (
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              className="absolute top-full left-0 mt-2 w-full max-h-60 overflow-y-auto bg-white border border-gray-200 rounded-md shadow-lg"
            >
              {searchResults.map((item) => (
                <div
                  key={item._id}
                  onClick={() => handleResultClick(item._id)}
                  className="flex items-center gap-3 p-3 hover:bg-gray-100 cursor-pointer transition-colors"
                >
                  <img
                    src={item.image}
                    alt={item.name}
                    className="w-10 h-10 object-cover rounded-md"
                  />
                  <div>
                    <p className="font-semibold">{item.name}</p>
                    <p className="text-sm text-gray-500">${Number(item.price).toFixed(2)}</p>
                  </div>
                </div>
              ))}
            </motion.div>
          )}
        </div>

        {/* ... (existing cart, auth links, etc.) ... */}
      </nav>
    </header>
  );
};

export default Header;
