import { useState, useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import "@fortawesome/fontawesome-free/css/all.min.css";
import axios from "axios";
import { initializeApp } from "https://www.gstatic.com/firebasejs/11.6.1/firebase-app.js";
import { getAuth, onAuthStateChanged, signInWithCustomToken, signInAnonymously } from "https://www.gstatic.com/firebasejs/11.6.1/firebase-auth.js";
import { getFirestore, collection, onSnapshot, doc, getDoc, setDoc } from "https://www.gstatic.com/firebasejs/11.6.1/firebase-firestore.js";

import Header from "./components/Header";
import Footer from "./components/Footer";
import Home from "./pages/Home";
import About from "./pages/About";
import Menu from "./pages/Menu";
import Orders from "./pages/Orders";
import Contact from "./pages/Contact";
import Cart from "./pages/ViewCart";
import Auth from "./pages/Auth";
import CreateMenu from "./pages/CreateMenu"; // New import

// Global variables for Firebase configuration, provided by the environment
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

function App() {
  const [user, setUser] = useState(null);
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [userId, setUserId] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [menuItems, setMenuItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Initialize Firebase and set up auth listener
    try {
      const app = initializeApp(firebaseConfig);
      const authInstance = getAuth(app);
      const dbInstance = getFirestore(app);
      setAuth(authInstance);
      setDb(dbInstance);

      onAuthStateChanged(authInstance, async (authUser) => {
        if (authUser) {
          // If a user is signed in, set the user and ID
          setUser(authUser);
          setUserId(authUser.uid);
        } else {
          // If no user, try to sign in with custom token or anonymously
          try {
            if (initialAuthToken) {
              await signInWithCustomToken(authInstance, initialAuthToken);
            } else {
              await signInAnonymously(authInstance);
            }
          } catch (error) {
            console.error("Firebase auth error:", error);
          }
        }
        setIsAuthReady(true);
      });
    } catch (error) {
      console.error("Firebase initialization failed:", error);
    }
  }, []);

  useEffect(() => {
    if (isAuthReady && db) {
      // Fetch menu items from the backend
      const fetchMenuItems = async () => {
        try {
          const response = await axios.get("http://localhost:5000/api/menu");
          if (Array.isArray(response.data)) {
            setMenuItems(response.data);
          } else {
            console.error("API did not return an array:", response.data);
            setMenuItems([]);
          }
        } catch (error) {
          console.error("Error fetching menu items:", error);
          setMenuItems([]);
        }
      };
      fetchMenuItems();
    }
  }, [isAuthReady, db]);

  const handleLogin = (userData) => {
    // This is a placeholder since Firebase is now handling auth.
    // The Auth component would handle the actual sign-in and the onAuthStateChanged listener would update the state.
    // For now, we'll just log the data and let the auth listener handle the rest.
    console.log("Login data received:", userData);
    // You would integrate this with Firebase sign-in methods like signInWithEmailAndPassword
  };

  const handleLogout = async () => {
    // Use the auth instance to sign out
    try {
      if (auth) {
        await auth.signOut();
        navigate("/auth");
      }
    } catch (error) {
      console.error("Error signing out:", error);
    }
  };

  const isAdmin = user?.role === "admin";

  return (
    <div className="font-poppins text-[#333] bg-white">
      <Header
        user={user}
        onLogout={handleLogout}
        isAdmin={isAdmin}
        searchQuery={searchQuery}
        setSearchQuery={setSearchQuery}
        menuItems={menuItems}
      />

      <main className="min-h-screen">
        <Routes>
          <Route path="/about" element={<About />} />
          <Route
            path="/menu"
            element={<Menu isAdmin={isAdmin} searchQuery={searchQuery} menuItems={menuItems} />}
          />
          <Route path="/orders" element={<Orders isAdmin={isAdmin} />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/auth" element={<Auth onLogin={handleLogin} />} />
          <Route path="/" element={<Home isAdmin={isAdmin} />} />

          {/* New route for admin menu creation */}
          <Route path="/admin/create-menu" element={<CreateMenu isAdmin={isAdmin} />} />
        </Routes>
      </main>

      <Footer />
    </div>
  );
}

export default App;
