import { Link } from "react-router-dom";
import { useEffect, useState } from "react";

export default function Header() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem("userInfo");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }

    // âœ… listen for login/logout
    window.addEventListener("storageChange", () => {
      const updatedUser = localStorage.getItem("userInfo");
      setUser(updatedUser ? JSON.parse(updatedUser) : null);
    });
  }, []);

  return (
    <header className="flex items-center justify-between py-[20px] px-[80px] sticky top-0 bg-white z-50">
      {/* Logo */}
      <div className="flex items-center gap-3">
        <img src="/logo.jpg" alt="SwadSeva Logo" className="w-[50px] h-[50px] rounded-full object-cover" />
        <span className="text-[1.5rem] font-bold text-[#333]">
          Swad<span className="food text-[orange]">Seva</span>
        </span>
      </div>

      {/* Navbar */}
      <nav>
        <ul className="flex list-none gap-[30px]">
          <li><Link to="/">HOME</Link></li>
          <li><Link to="/about">ABOUT US</Link></li>
          <li><Link to="/menu">MENU</Link></li>
          <li><Link to="/orders">ORDERS</Link></li>
          <li><Link to="/contact">CONTACT</Link></li>
        </ul>
      </nav>

      {/* Search */}
      <form className="new-search flex items-center bg-white rounded-[10px] gap-3 max-w-[500px] p-[10px_90px]">
        <i className="fas fa-map-marker-alt text-[#f26522] text-[18px]"></i>
        <input type="text" placeholder="Search food near you..." className="flex-1 border-none outline-none text-[1rem]" />
      </form>

      {/* Auth Section */}
      <div className="auth flex items-center gap-4">
        {user ? (
          <span className="font-medium text-[#333]">ðŸ‘‹ Welcome, {user.username}</span>
        ) : (
          <Link to="/auth" className="px-[8px] py-[8px] border-2 border-[orange] rounded text-[orange] font-medium">
            SIGN UP
          </Link>
        )}

        <Link to="/cart" className="px-[8px] py-[8px] border-2 border-[orange] rounded text-[orange]">
          ðŸ›’ CART
        </Link>
      </div>
    </header>
  );
}
