// src/pages/AdminMenuForm.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useLocation, useNavigate } from "react-router-dom";

const AdminMenuForm = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const itemToUpdate = location.state?.itemToUpdate;

  const [formData, setFormData] = useState({
    foodName: "",
    imageURL: "",
    rating: "",
    reviews: "",
    description: "",
    price: "",
    category: "",
    restaurantName: "",
    restaurantLogo: "",
  });

  useEffect(() => {
    if (itemToUpdate) {
      setFormData({
        foodName: itemToUpdate.name || "",
        imageURL: itemToUpdate.image || "",
        rating: itemToUpdate.ratings || "",
        reviews: itemToUpdate.reviews || "",
        description: itemToUpdate.description || "",
        price: itemToUpdate.price || "",
        category: itemToUpdate.category || "",
        restaurantName: itemToUpdate.restaurantName || "",
        restaurantLogo: itemToUpdate.restaurantLogo || "",
      });
    }
  }, [itemToUpdate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${JSON.parse(localStorage.getItem("userInfo")).token}`,
        },
      };

      const payload = {
        name: formData.foodName,
        image: formData.imageURL,
        ratings: formData.rating,
        reviews: formData.reviews,
        description: formData.description,
        price: parseFloat(formData.price),
        category: formData.category,
        restaurantName: formData.restaurantName,
        restaurantLogo: formData.restaurantLogo,
      };

      if (itemToUpdate) {
        // Update existing item using PUT request
        await axios.put(
          `http://localhost:5000/api/menu/${itemToUpdate._id}`,
          payload,
          config
        );
        toast.success("Menu item updated successfully!");
      } else {
        // Create new item using POST request
        await axios.post("http://localhost:5000/api/menu", payload, config);
        toast.success("Menu item created successfully!");
      }

      navigate("/menu");
      // Optional: Force a reload to see the changes immediately
      window.location.reload();
    } catch (error) {
      toast.error(
        `Error: ${error.response?.data?.message || error.message}`
      );
    }
  };

  return (
    <div className="container mx-auto p-4 md:p-8">
      <div className="max-w-xl mx-auto bg-white shadow-xl rounded-lg p-6 md:p-8">
        <h2 className="text-2xl md:text-3xl font-bold text-center text-orange-600 mb-6">
          {itemToUpdate ? "Update Menu Item" : "Create New Menu Item"}
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Food Name */}
          <div>
            <label className="block text-gray-700 font-medium">Food Name</label>
            <input
              type="text"
              name="foodName"
              value={formData.foodName}
              onChange={handleChange}
              placeholder="e.g., Spicy Pizza"
              required
              className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
            />
          </div>
          {/* Image URL */}
          <div>
            <label className="block text-gray-700 font-medium">Image URL</label>
            <input
              type="text"
              name="imageURL"
              value={formData.imageURL}
              onChange={handleChange}
              placeholder="e.g., https://example.com/image.jpg"
              className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
            />
          </div>
          {/* Rating */}
          <div>
            <label className="block text-gray-700 font-medium">Rating</label>
            <input
              type="number"
              name="rating"
              value={formData.rating}
              onChange={handleChange}
              placeholder="e.g., 4.5"
              step="0.1"
              min="0"
              max="5"
              className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
            />
          </div>
          {/* Reviews */}
          <div>
            <label className="block text-gray-700 font-medium">Reviews</label>
            <input
              type="number"
              name="reviews"
              value={formData.reviews}
              onChange={handleChange}
              placeholder="e.g., 120"
              min="0"
              className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
            />
          </div>
          {/* Description */}
          <div>
            <label className="block text-gray-700 font-medium">Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="A brief description of the food item."
              rows="3"
              className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
            ></textarea>
          </div>
          {/* Price */}
          <div>
            <label className="block text-gray-700 font-medium">Price ($)</label>
            <input
              type="number"
              name="price"
              value={formData.price}
              onChange={handleChange}
              placeholder="e.g., 15.99"
              step="0.01"
              required
              className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
            />
          </div>
          {/* Category */}
          <div>
            <label className="block text-gray-700 font-medium">Category</label>
            <select
              name="category"
              value={formData.category}
              onChange={handleChange}
              required
              className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
            >
              <option value="">Select a category</option>
              <option value="Burger">Burger</option>
              <option value="Pizza">Pizza</option>
              <option value="Biriyani">Biriyani</option>
              <option value="Dessert">Dessert</option>
              <option value="Cake">Cake</option>
              <option value="Beverage">Beverage</option>
            </select>
          </div>
          {/* Restaurant Name */}
          <div>
            <label className="block text-gray-700 font-medium">
              Restaurant Name
            </label>
            <input
              type="text"
              name="restaurantName"
              value={formData.restaurantName}
              onChange={handleChange}
              placeholder="e.g., SwadSeva Restaurant"
              className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
            />
          </div>
          {/* Restaurant Logo */}
          <div>
            <label className="block text-gray-700 font-medium">
              Restaurant Logo URL
            </label>
            <input
              type="text"
              name="restaurantLogo"
              value={formData.restaurantLogo}
              onChange={handleChange}
              placeholder="e.g., https://example.com/logo.png"
              className="w-full mt-1 p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-orange-500"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-orange-600 text-white font-semibold py-3 px-6 rounded-md transition-colors hover:bg-orange-700"
          >
            {itemToUpdate ? "Update Item" : "Create Menu Item"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AdminMenuForm;
