import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import AddToCartButton from "../components/AddToCart";
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import { toast } from "react-toastify";

// The Menu component now receives the isAdmin and searchQuery props
const Menu = ({ isAdmin, searchQuery }) => {
  const [menuItems, setMenuItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("All");
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/menu")
      .then((res) => {
        if (!res.ok) {
          throw new Error('Network response was not ok');
        }
        return res.json();
      })
      .then((data) => {
        if (Array.isArray(data)) {
          setMenuItems(data);
        } else {
          console.error("API did not return an array:", data);
          setMenuItems([]);
        }
      })
      .catch((err) => {
        console.error("Error fetching menu:", err);
        setMenuItems([]);
      });
  }, []);

  const handleDelete = async (itemId) => {
    if (window.confirm("Are you sure you want to delete this item?")) {
      try {
        await axios.delete(`http://localhost:5000/api/menu/${itemId}`);
        setMenuItems(menuItems.filter((item) => item._id !== itemId));
        toast.success("Item deleted successfully!");
      } catch (error) {
        console.error("Error deleting item:", error);
        toast.error("Failed to delete item.");
      }
    }
  };

  const handleUpdate = (item) => {
    navigate('/admin/create-menu', { state: { itemToUpdate: item } });
  };

  const categories = [
    { name: "All", img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-food-meal-png-image_10202029.png" },
    { name: "Burger", img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-food-meal-png-image_10202029.png" },
    { name: "Pizza", img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-food-meal-png-image_10202029.png" },
    { name: "Pasta", img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-food-meal-png-image_10202029.png" },
    { name: "Biriyani", img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-food-meal-png-image_10202029.png" },
    { name: "Drinks", img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-food-meal-png-image_10202029.png" },
    { name: "Chinese", img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-food-meal-png-image_10202029.png" },
  ];

  // The filtering logic now combines category and search query
  const filteredItems = menuItems.filter((item) => {
    const matchesCategory =
      selectedCategory === "All" ||
      item.category?.toLowerCase().trim() === selectedCategory.toLowerCase().trim();
    
    const matchesSearch =
      searchQuery.trim() === "" ||
      item.name.toLowerCase().includes(searchQuery.toLowerCase().trim()) ||
      item.description?.toLowerCase().includes(searchQuery.toLowerCase().trim());

    return matchesCategory && matchesSearch;
  });

  return (
    <section id="menu-section" className="max-w-[1000px] mx-auto p-8">
      <h2 className="text-[2rem] font-bold mb-6 text-center">
        Explore Our <span className="brand text-[#e96610]">Menu</span>
      </h2>

      {/* CATEGORIES */}
      <div className="flex justify-center flex-wrap gap-4 mb-8">
        {categories.map((category) => (
          <motion.div
            key={category.name}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => setSelectedCategory(category.name)}
            className={`cursor-pointer p-4 rounded-lg flex flex-col items-center border-2 transition-colors ${selectedCategory === category.name ? "border-[#e96610]" : "border-transparent"}`}
          >
            <img src={category.img} alt={category.name} className="w-[60px] h-[60px] object-cover rounded-full mb-2" />
            <span className="text-sm font-semibold">{category.name}</span>
          </motion.div>
        ))}
      </div>

      {/* MENU ITEMS */}
      <div className="menu-items-grid grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <motion.div
              key={item._id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              className="bg-white rounded-[12px] shadow-[0_4px_15px_rgba(0,0,0,0.08)] overflow-hidden flex flex-col hover:shadow-xl transition-shadow"
            >
              {/* Image */}
              <img
                src={
                  item.image ||
                  "https://via.placeholder.com/400x300.png?text=No+Image"
                }
                alt={item.name}
                className="w-full h-[155px] object-cover"
              />

              {/* Details */}
              <div className="card-info p-3 flex-1">
                <div className="name-reviews flex items-center gap-3">
                  <h2 className="text-[18px] font-semibold leading-snug">
                    {item.name}
                  </h2>
                  <div className="reviews text-[#ff9800] text-[13px]">
                    ⭐ {Number(item.ratings ?? 0).toFixed(1)}{" "}
                    <span className="text-[#555] ml-2">
                      ({(item.reviews ?? 0).toLocaleString()} reviews)
                    </span>
                  </div>
                </div>

                <p className="text-[14px] text-[#555] leading-[1.4] mt-2">
                  {item.description}
                </p>

                <div className="price mt-3 font-bold">
                  ${Number(item.price ?? 0).toFixed(2)}
                </div>
              </div>

              {/* Conditional rendering of buttons based on isAdmin prop */}
              {isAdmin ? (
                <div className="flex justify-between p-3 gap-3">
                  <button
                    onClick={() => handleUpdate(item)}
                    className="flex-1 bg-blue-500 text-white rounded-md py-2 px-4 transition-colors hover:bg-blue-600"
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(item._id)}
                    className="flex-1 bg-red-500 text-white rounded-md py-2 px-4 transition-colors hover:bg-red-600"
                  >
                    Delete
                  </button>
                </div>
              ) : (
                <AddToCartButton item={item} />
              )}
            </motion.div>
          ))
        ) : (
          <p className="text-center text-gray-500 col-span-full">No menu items to display.</p>
        )}
      </div>
    </section>
  );
};

export default Menu;
