import React, { useState, useEffect } from "react";

export default function Orders({ isAdmin }) {
  const [menuItems, setMenuItems] = useState([]);
  const [newItem, setNewItem] = useState({
    name: "",
    description: "",
    price: "",
    image: "",
  });
  const [editingId, setEditingId] = useState(null);

  // ✅ Fetch menu items
  useEffect(() => {
    fetch("http://localhost:5000/api/menu")
      .then((res) => res.json())
      .then((data) => setMenuItems(data))
      .catch((err) => console.error("Error fetching menu:", err));
  }, []);

  // ✅ Handle form input
  const handleChange = (e) => {
    setNewItem({ ...newItem, [e.target.name]: e.target.value });
  };

  // ✅ Add menu item
  const handleAdd = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/menu", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newItem),
      });
      const data = await res.json();
      setMenuItems([...menuItems, data]); // append new item
      setNewItem({ name: "", description: "", price: "", image: "" });
    } catch (error) {
      console.error("Error adding menu item:", error);
    }
  };

  // ✅ Delete menu item
  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/menu/${id}`, { method: "DELETE" });
      setMenuItems(menuItems.filter((item) => item._id !== id));
    } catch (error) {
      console.error("Error deleting menu item:", error);
    }
  };

  // ✅ Start editing
  const handleEdit = (item) => {
    setEditingId(item._id);
    setNewItem({
      name: item.name,
      description: item.description,
      price: item.price,
      image: item.image,
    });
  };

  // ✅ Update menu item
  const handleUpdate = async () => {
    try {
      const res = await fetch(`http://localhost:5000/api/menu/${editingId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newItem),
      });
      const data = await res.json();
      setMenuItems(
        menuItems.map((item) => (item._id === editingId ? data : item))
      );
      setEditingId(null);
      setNewItem({ name: "", description: "", price: "", image: "" });
    } catch (error) {
      console.error("Error updating menu item:", error);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Admin Menu Management</h1>

      {/* ✅ Form for add/edit */}
      <div className="bg-white shadow-md rounded p-4 mb-6">
        <input
          type="text"
          name="name"
          value={newItem.name}
          onChange={handleChange}
          placeholder="Item Name"
          className="border p-2 rounded w-full mb-2"
        />
        <input
          type="text"
          name="description"
          value={newItem.description}
          onChange={handleChange}
          placeholder="Description"
          className="border p-2 rounded w-full mb-2"
        />
        <input
          type="number"
          name="price"
          value={newItem.price}
          onChange={handleChange}
          placeholder="Price"
          className="border p-2 rounded w-full mb-2"
        />
        <input
          type="text"
          name="image"
          value={newItem.image}
          onChange={handleChange}
          placeholder="Image URL"
          className="border p-2 rounded w-full mb-2"
        />

        {editingId ? (
          <button
            onClick={handleUpdate}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Update Item
          </button>
        ) : (
          <button
            onClick={handleAdd}
            className="bg-green-500 text-white px-4 py-2 rounded"
          >
            Create Menu Item
          </button>
        )}
      </div>

      {/* ✅ Menu list */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {menuItems.map((item) => (
          <div
            key={item._id}
            className="border rounded p-4 shadow-sm bg-white flex flex-col"
          >
            <img
              src={item.image}
              alt={item.name}
              className="h-32 object-cover mb-2 rounded"
            />
            <h2 className="font-bold text-lg">{item.name}</h2>
            <p className="text-sm text-gray-600">{item.description}</p>
            <p className="font-semibold mt-1">${item.price}</p>
            <div className="mt-2 flex gap-2">
              <button
                onClick={() => handleEdit(item)}
                className="bg-yellow-500 text-white px-3 py-1 rounded"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(item._id)}
                className="bg-red-500 text-white px-3 py-1 rounded"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
