import React, { useState, useEffect } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { toast } from "react-toastify";

const Header = () => {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState("user"); // default user
  const navigate = useNavigate();

  useEffect(() => {
    const checkUser = () => {
      const userInfo = localStorage.getItem("userInfo");
      if (userInfo) {
        const parsed = JSON.parse(userInfo);
        setUser(parsed.username);
        setRole(parsed.role || "user");
      } else {
        setUser(null);
        setRole("user");
      }
    };

    checkUser();
    window.addEventListener("storageChange", checkUser);
    return () => window.removeEventListener("storageChange", checkUser);
  }, []);

  const handleSignOut = () => {
    localStorage.removeItem("userInfo");
    setUser(null);
    setRole("user");
    toast.info("You have been signed out.");
    navigate("/auth");
    window.dispatchEvent(new Event("storageChange"));
  };

  const activeClass = "text-orange-600 font-bold";

  return (
    <header className="flex items-center justify-between py-5 px-20 sticky top-0 bg-white z-50 shadow-md">
      <div className="flex items-center gap-3">
        <img
          src="/logo.jpg"
          alt="SwadSeva Logo"
          className="w-12 h-12 rounded-full object-cover"
        />
        <span className="text-2xl font-bold text-[#333]">
          Swad<span className="text-orange-600">Seva</span>
        </span>
      </div>

      <nav>
        <ul className="flex gap-8">
          <li>
            <NavLink
              to="/"
              className={({ isActive }) => (isActive ? activeClass : "text-[#333]")}
            >
              HOME
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/about"
              className={({ isActive }) => (isActive ? activeClass : "text-[#333]")}
            >
              ABOUT US
            </NavLink>
          </li>

          {role === "admin" ? (
            <>
              <li>
                <NavLink
                  to="/menu"
                  className={({ isActive }) => (isActive ? activeClass : "text-[#333]")}
                >
                  MENU
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/create-menu"
                  className={({ isActive }) => (isActive ? activeClass : "text-[#333]")}
                >
                  CREATE MENU
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/messages"
                  className={({ isActive }) => (isActive ? activeClass : "text-[#333]")}
                >
                  MESSAGES
                </NavLink>
              </li>
            </>
          ) : (
            <>
              <li>
                <NavLink
                  to="/menu"
                  className={({ isActive }) => (isActive ? activeClass : "text-[#333]")}
                >
                  MENU
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/orders"
                  className={({ isActive }) => (isActive ? activeClass : "text-[#333]")}
                >
                  ORDERS
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/contact"
                  className={({ isActive }) => (isActive ? activeClass : "text-[#333]")}
                >
                  CONTACT
                </NavLink>
              </li>
            </>
          )}
        </ul>
      </nav>

      <div className="flex items-center gap-4">
        <form className="relative">
          <i className="fas fa-search absolute left-3 top-2 text-gray-400"></i>
          <input
            type="text"
            placeholder="Search food..."
            className="pl-10 pr-4 py-2 rounded-full border border-gray-300 focus:outline-none focus:ring-2 focus:ring-orange-500"
          />
        </form>

        {role !== "admin" && (
          <NavLink to="/cart" className="relative text-gray-600 hover:text-orange-600 transition">
            <i className="fas fa-shopping-cart text-xl"></i>
          </NavLink>
        )}

        {user ? (
          <>
            <motion.span
              initial={{ opacity: 0, x: -10 }}
              animate={{ opacity: 1, x: 0 }}
              className="text-gray-800 font-medium"
            >
              Welcome, {user}!
            </motion.span>
            <button
              onClick={handleSignOut}
              className="text-white bg-orange-600 hover:bg-orange-700 py-2 px-4 rounded-full font-medium transition"
            >
              Sign Out
            </button>
          </>
        ) : (
          <NavLink
            to="/auth"
            className="text-white bg-orange-600 hover:bg-orange-700 py-2 px-4 rounded-full font-medium transition"
          >
            Sign In / Sign Up
          </NavLink>
        )}
      </div>
    </header>
  );
};

export default Header;
