import React, { useState, useMemo } from "react";
import { motion } from "framer-motion";
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import { toast } from "react-toastify";

// A self-contained AddToCartButton component
const AddToCartButton = ({ item }) => {
  const [adding, setAdding] = useState(false);

  const handleAddToCart = async () => {
    setAdding(true);
    try {
      // Simulate adding to cart logic
      console.log(`Adding item: ${item.name} to cart.`);
      toast.success(`${item.name} added to cart!`);
    } catch (error) {
      console.error("Failed to add to cart:", error);
      toast.error("Failed to add item to cart.");
    } finally {
      setAdding(false);
    }
  };

  return (
    <button
      onClick={handleAddToCart}
      disabled={adding}
      className="w-full bg-orange-500 text-white py-2 px-4 rounded-[8px] font-bold text-lg transition-transform transform hover:scale-105 active:scale-95 disabled:bg-gray-400 disabled:cursor-not-allowed shadow-md"
    >
      {adding ? "Adding..." : "Add to Cart"}
    </button>
  );
};

// The Menu component now receives the menuItems and searchQuery props
const Menu = ({ isAdmin, searchQuery, menuItems }) => {
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const [generatedDescriptions, setGeneratedDescriptions] = useState({});
  const [isGenerating, setIsGenerating] = useState({});
  const [isSpeaking, setIsSpeaking] = useState({});
  const navigate = useNavigate();
  
  // TTS API Helper Functions
  const base64ToArrayBuffer = (base64) => {
    const binaryString = atob(base64);
    const len = binaryString.length;
    const bytes = new Uint8Array(len);
    for (let i = 0; i < len; i++) {
        bytes[i] = binaryString.charCodeAt(i);
    }
    return bytes.buffer;
  };
  
  const pcmToWav = (pcmData, sampleRate) => {
    const pcm16 = pcmData;
    const dataLength = pcm16.length * 2;
    const buffer = new ArrayBuffer(44 + dataLength);
    const view = new DataView(buffer);

    const writeString = (view, offset, string) => {
        for (let i = 0; i < string.length; i++) {
            view.setUint8(offset + i, string.charCodeAt(i));
        }
    };

    let offset = 0;
    writeString(view, offset, 'RIFF'); offset += 4;
    view.setUint32(offset, 36 + dataLength, true); offset += 4;
    writeString(view, offset, 'WAVE'); offset += 4;
    writeString(view, offset, 'fmt '); offset += 4;
    view.setUint32(offset, 16, true); offset += 4;
    view.setUint16(offset, 1, true); offset += 2;
    view.setUint16(offset, 1, true); offset += 2;
    view.setUint32(offset, sampleRate, true); offset += 4;
    view.setUint32(offset, sampleRate * 2, true); offset += 4;
    view.setUint16(offset, 2, true); offset += 2;
    view.setUint16(offset, 16, true); offset += 2;
    writeString(view, offset, 'data'); offset += 4;
    view.setUint32(offset, dataLength, true); offset += 4;

    for (let i = 0; i < pcm16.length; i++) {
        view.setInt16(offset + i * 2, pcm16[i], true);
    }

    return new Blob([view], { type: 'audio/wav' });
  };
  
  // Generates a descriptive text for a menu item using Gemini API
  const handleGenerateDescription = async (item, itemId) => {
    setIsGenerating(prev => ({ ...prev, [itemId]: true }));
    const systemPrompt = `You are a professional food blogger and menu writer. Write a short, single-paragraph, enticing and delicious-sounding description for the following food item. Do not include a title or any other text besides the description itself.`;
    const userQuery = `Food Item Name: ${item.name}. Category: ${item.category}. Description: ${item.description}.`;
    
    const payload = {
      contents: [{ parts: [{ text: userQuery }] }],
      systemInstruction: {
        parts: [{ text: systemPrompt }]
      },
    };

    const apiKey = "";
    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
    
    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();
      const generatedText = result.candidates?.[0]?.content?.parts?.[0]?.text;
      if (generatedText) {
        setGeneratedDescriptions(prev => ({ ...prev, [itemId]: generatedText }));
        toast.success("New description generated!");
      } else {
        toast.error("Failed to generate description.");
      }
    } catch (error) {
      console.error("Error calling Gemini API:", error);
      toast.error("Failed to generate description.");
    } finally {
      setIsGenerating(prev => ({ ...prev, [itemId]: false }));
    }
  };
  
  // Reads the item's name and description out loud using Gemini TTS API
  const handleReadAloud = async (item) => {
    setIsSpeaking(prev => ({ ...prev, [item._id]: true }));
    const textToSpeak = `${item.name}. ${generatedDescriptions[item._id] || item.description}`;
    
    const payload = {
        contents: [{ parts: [{ text: textToSpeak }] }],
        generationConfig: {
            responseModalities: ["AUDIO"],
            speechConfig: {
                voiceConfig: {
                    prebuiltVoiceConfig: { voiceName: "Zephyr" }
                }
            }
        },
        model: "gemini-2.5-flash-preview-tts"
    };
    
    const apiKey = ""; 
    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;
    
    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      
      const result = await response.json();
      const part = result?.candidates?.[0]?.content?.parts?.[0];
      const audioData = part?.inlineData?.data;
      const mimeType = part?.inlineData?.mimeType;

      if (audioData && mimeType && mimeType.startsWith("audio/")) {
        const sampleRate = parseInt(mimeType.match(/rate=(\d+)/)[1], 10);
        const pcmData = base64ToArrayBuffer(audioData);
        const pcm16 = new Int16Array(pcmData);
        const wavBlob = pcmToWav(pcm16, sampleRate);
        const audioUrl = URL.createObjectURL(wavBlob);
        
        const audio = new Audio(audioUrl);
        audio.play().catch(e => console.error("Audio play failed:", e));
        audio.onended = () => {
          URL.revokeObjectURL(audioUrl);
          setIsSpeaking(prev => ({ ...prev, [item._id]: false }));
        };
      } else {
        toast.error("Failed to read aloud.");
      }
    } catch (error) {
      console.error("Error calling TTS API:", error);
      toast.error("Failed to read aloud.");
    } finally {
      setIsSpeaking(prev => ({ ...prev, [item._id]: false }));
    }
  };

  // Use useMemo for performance optimization
  const filteredItems = useMemo(() => {
    // We'll create a mock menuItems array to show the new restaurant info.
    // In a real app, this data would come from an API or database.
    const mockMenuItems = [
      {
        _id: "1",
        name: "Classic Cheeseburger",
        category: "Burger",
        price: 9.99,
        ratings: 4.8,
        reviews: 245,
        description: "A juicy beef patty topped with melted cheddar, fresh lettuce, tomato, and a special sauce on a toasted bun.",
        image: "https://images.unsplash.com/photo-1571091718767-18b5b1457add?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        restaurantName: "Burger King",
        restaurantImage: "https://upload.wikimedia.org/wikipedia/commons/thumb/c/c2/Burger_King_logo_%281999%29.svg/2048px-Burger_King_logo_%281999%29.svg.png"
      },
      {
        _id: "2",
        name: "Pepperoni Pizza",
        category: "Pizza",
        price: 15.50,
        ratings: 4.5,
        reviews: 198,
        description: "A classic pizza topped with zesty tomato sauce, mozzarella cheese, and generous slices of savory pepperoni.",
        image: "https://images.unsplash.com/photo-1593560704563-f1a6f1d2d0b5?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        restaurantName: "Domino's",
        restaurantImage: "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3e/Domino%27s_pizza_logo.svg/2048px-Domino%27s_pizza_logo.svg.png"
      },
      {
        _id: "3",
        name: "Chocolate Lava Cake",
        category: "Dessert",
        price: 8.00,
        ratings: 4.9,
        reviews: 312,
        description: "A rich, moist chocolate cake with a molten center, served warm with a scoop of vanilla ice cream.",
        image: "https://images.unsplash.com/photo-1596700810769-1c97a87756f4?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        restaurantName: "The Cheesecake Factory",
        restaurantImage: "https://logos-world.net/wp-content/uploads/2020/12/The-Cheesecake-Factory-Logo.png"
      },
      {
        _id: "4",
        name: "Chicken Biryani",
        category: "Biriyani",
        price: 12.99,
        ratings: 4.7,
        reviews: 150,
        description: "A fragrant rice dish cooked with succulent pieces of marinated chicken and a blend of aromatic spices.",
        image: "https://images.unsplash.com/photo-1614352882269-8051287c8088?q=80&w=2832&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        restaurantName: "Paradise Biryani",
        restaurantImage: "https://upload.wikimedia.org/wikipedia/en/e/e4/Paradise_Food_Court_logo.png"
      },
      {
        _id: "5",
        name: "Espresso Frappuccino",
        category: "Beverage",
        price: 6.50,
        ratings: 4.6,
        reviews: 89,
        description: "A cold, blended coffee drink made with rich espresso, milk, and ice, topped with whipped cream and a drizzle of caramel.",
        image: "https://images.unsplash.com/photo-1589178224794-550ff4898144?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        restaurantName: "Starbucks",
        restaurantImage: "https://upload.wikimedia.org/wikipedia/en/thumb/d/d3/Starbucks_Corporation_Logo_2011.svg/1200px-Starbucks_Corporation_Logo_2011.svg.png"
      },
      {
        _id: "6",
        name: "Red Velvet Cake",
        category: "Cake",
        price: 7.25,
        ratings: 4.9,
        reviews: 205,
        description: "A beautifully layered red velvet cake with cream cheese frosting, a perfect treat for any occasion.",
        image: "https://images.unsplash.com/photo-1611295240217-ee8d960146f3?q=80&w=2835&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        restaurantName: "Magnolia Bakery",
        restaurantImage: "https://www.magnoliabakery.com/cdn/shop/files/Magnolia_2x.png?v=1646244675&width=200"
      },
      {
        _id: "7",
        name: "French Fries",
        category: "Burger",
        price: 3.50,
        ratings: 4.5,
        reviews: 99,
        description: "Crispy, golden-brown french fries, perfectly salted and served hot.",
        image: "https://images.unsplash.com/photo-1599736855342-69f82d1c68f1?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        restaurantName: "McDonald's",
        restaurantImage: "https://upload.wikimedia.org/wikipedia/commons/thumb/3/36/McDonald%27s_Golden_Arches.svg/1200px-McDonald%27s_Golden_Arches.svg.png"
      }
    ];

    const allItems = [...menuItems, ...mockMenuItems];
    
    const searchedItems = searchQuery.length > 0
      ? allItems.filter(item =>
          item.name.toLowerCase().includes(searchQuery.toLowerCase())
        )
      : allItems;

    return selectedCategory === "All"
      ? searchedItems
      : searchedItems.filter(item => item.category === selectedCategory);
  }, [menuItems, searchQuery, selectedCategory]);

  const categories = [
    { name: "All", img: "https://png.pngtree.com/png-vector/20231018/ourmid/pngtree-fast-foods-item-png-image_10303953.png" },
    { name: "Burger", img: "https://png.pngtree.com/png-vector/20231016/ourmid/pngtree-burger-food-sticker-png-png-image_10188397.png" },
    { name: "Pizza", img: "https://png.pngtree.com/png-clipart/20230916/original/pngtree-sticker-with-a-cartoon-pizza-sticker-isolated-on-white-vector-png-image_12229935.png" },
    { name: "Cake", img: "https://static.vecteezy.com/system/resources/thumbnails/046/484/390/small_2x/birthday-cake-with-chocolate-and-strawberry-ai-generative-free-png.png" },
    { name: "Dessert", img: "https://static.vecteezy.com/system/resources/previews/024/657/382/non_2x/trifle-cake-dessert-hand-drawn-sticker-sweet-and-delicious-with-fruit-topping-on-top-free-png.png" },
    { name: "Biriyani", img: "https://static.vecteezy.com/system/resources/previews/027/144/452/non_2x/delicious-chicken-biryani-isolated-on-transparent-background-png.png" },
    { name: "Beverage", img: "https://png.pngtree.com/png-clipart/20230916/original/pngtree-sticker-with-a-cocacola-drink-spilling-in-it-clipart-vector-png-image_12234714.png" },
  ];

  const handleDelete = (itemId) => {
    setItemToDelete(itemId);
    setIsModalOpen(true);
  };

  const confirmDelete = async () => {
    try {
      const userInfo = JSON.parse(localStorage.getItem("userInfo"));
      const config = {
        headers: {
          Authorization: `Bearer ${userInfo?.token}`,
        },
      };
      await axios.delete(`http://localhost:5000/api/menu/${itemToDelete}`, config);
      toast.success("Menu item deleted successfully!");
      window.location.reload(); 
    } catch (err) {
      console.error("Error deleting menu item:", err);
      toast.error("Failed to delete menu item.");
    } finally {
      setIsModalOpen(false);
      setItemToDelete(null);
    }
  };

  const cancelDelete = () => {
    setIsModalOpen(false);
    setItemToDelete(null);
  };

  const handleUpdate = (item) => {
    navigate('/admin/create-menu', { state: { itemToUpdate: item } });
  };

  return (
    <section className="bg-[#fff3eb] py-[40px]">
      {/* Heading */}
      <h1 className="text-[2rem] font-bold text-center mb-6">
        Explore Our{" "}
        <span className="brand text-[color:#e96610] italic">
          Delicious Offerings
        </span>
      </h1>

      {/* Categories */}
      <div className="categories flex justify-center items-center gap-5 overflow-x-auto px-6 py-2 mb-10">
        {categories.map((cat) => (
          <motion.div
            key={cat.name}
            whileTap={{ scale: 0.9 }}
            whileHover={{ scale: 1.1 }}
            onClick={() => setSelectedCategory(cat.name)}
            className={`category flex flex-col items-center bg-[#fff3eb] p-2 rounded-[12px] cursor-pointer border-2 ${
              selectedCategory === cat.name
                ? "border-[orange] shadow-lg shadow-orange-300"
                : "border-transparent"
            } transition`}
          >
            <img
              src={cat.img}
              alt={cat.name}
              className="w-[80px] h-[80px] mb-2"
            />
            <p className="font-medium">{cat.name}</p>
          </motion.div>
        ))}
      </div>

      {/* Menu Items */}
      <div className="p-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {Array.isArray(filteredItems) && filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <motion.div
              key={item._id}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
              className="card bg-white rounded-[12px] overflow-hidden shadow-[0_4px_12px_rgba(0,0,0,0.08)] flex flex-col"
            >
              {/* Image */}
              <img
                src={
                  item.image ||
                  "https://via.placeholder.com/600x400?text=No+Image"
                }
                alt={item.name}
                className="w-full h-[155px] object-cover"
              />

              {/* Details */}
              <div className="card-info p-3 flex-1">
                {/* Name, Restaurant Name, Rating, Reviews */}
                <div className="name-reviews flex flex-col gap-1 mb-2">
                  <div className="flex justify-between items-center w-full">
                     <h2 className="text-[18px] font-semibold leading-snug">{item.name}</h2>
                     <div className="flex items-center gap-2">
                        {item.restaurantName && (
                          <p className="text-sm text-gray-600 font-medium whitespace-nowrap">
                            {item.restaurantName}
                          </p>
                        )}
                        {item.restaurantImage && (
                          <img 
                            src={item.restaurantImage} 
                            alt={`${item.restaurantName} logo`} 
                            className="w-6 h-6 rounded-full object-cover" 
                          />
                        )}
                        {!isAdmin && (
                            <button 
                              onClick={() => handleReadAloud(item)}
                              disabled={isSpeaking[item._id]}
                              className="text-gray-600 hover:text-orange-500 transition-colors"
                            >
                              {isSpeaking[item._id] ? "..." : "ðŸ”Š"}
                            </button>
                        )}
                     </div>
                  </div>
                  <div className="reviews text-[#ff9800] text-[13px] flex items-center gap-2 mt-1">
                    <span className="flex items-center">
                      â­ {Number(item.ratings ?? 0).toFixed(1)}
                    </span>
                    <span className="text-[#555]">
                      ({(item.reviews ?? 0).toLocaleString()} reviews)
                    </span>
                  </div>
                </div>

                {/* Description and Gemini API button */}
                <p className="text-[14px] text-[#555] leading-[1.4] min-h-[50px]">
                  {generatedDescriptions[item._id] || item.description}
                </p>
                {!isAdmin && (
                  <button
                    onClick={() => handleGenerateDescription(item, item._id)}
                    disabled={isGenerating[item._id]}
                    className="mt-2 text-sm text-blue-500 hover:text-blue-700 font-medium transition-colors"
                  >
                    {isGenerating[item._id] ? 'Generating...' : 'âœ¨ Generate Description'}
                  </button>
                )}

                {/* Price */}
                <div className="price mt-3 font-bold">
                  ${Number(item.price ?? 0).toFixed(2)}
                </div>
              </div>

              {/* Conditional rendering of buttons based on isAdmin prop */}
              {isAdmin ? (
                <div className="flex justify-between p-3 gap-3">
                  <button
                    onClick={() => handleUpdate(item)}
                    className="flex-1 bg-blue-500 text-white rounded-md py-2 px-4 transition-colors hover:bg-blue-600"
                  >
                    Update
                  </button>
                  <button
                    onClick={() => handleDelete(item._id)}
                    className="flex-1 bg-red-500 text-white rounded-md py-2 px-4 transition-colors hover:bg-red-600"
                  >
                    Delete
                  </button>
                </div>
              ) : (
                <AddToCartButton item={item} />
              )}
            </motion.div>
          ))
        ) : (
          <p className="text-center text-gray-500 col-span-full">No menu items to display.</p>
        )}
      </div>

      {/* Custom Confirmation Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl text-center">
            <h3 className="text-xl font-bold mb-4">Confirm Deletion</h3>
            <p className="text-gray-700 mb-6">Are you sure you want to delete this item?</p>
            <div className="flex justify-center gap-4">
              <button
                onClick={confirmDelete}
                className="bg-red-500 text-white py-2 px-4 rounded-md hover:bg-red-600 transition-colors"
              >
                Yes, Delete
              </button>
              <button
                onClick={cancelDelete}
                className="bg-gray-300 text-gray-800 py-2 px-4 rounded-md hover:bg-gray-400 transition-colors"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </section>
  );
};

export default Menu;
