import React, { useState, useMemo } from "react";
import { motion } from "framer-motion";
import { useNavigate } from 'react-router-dom';
import axios from "axios";
import { toast } from "react-toastify";

// A self-contained AddToCartButton component
const AddToCartButton = ({ item }) => {
  const [adding, setAdding] = useState(false);

  const handleAddToCart = async () => {
    setAdding(true);
    try {
      // Simulate adding to cart logic
      console.log(`Adding item: ${item.name} to cart.`);
      toast.success(`${item.name} added to cart!`);
    } catch (error) {
      console.error("Failed to add to cart:", error);
      toast.error("Failed to add item to cart.");
    } finally {
      setAdding(false);
    }
  };

  return (
    <button
      onClick={handleAddToCart}
      disabled={adding}
      className="w-full bg-orange-500 text-white py-2 px-4 rounded-[8px] font-bold text-lg transition-transform transform hover:scale-105 active:scale-95 disabled:bg-gray-400 disabled:cursor-not-allowed shadow-md"
    >
      {adding ? "Adding..." : "Add to Cart"}
    </button>
  );
};

// The Menu component now receives the menuItems and searchQuery props
const Menu = ({ isAdmin, searchQuery, menuItems }) => {
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);
  const navigate = useNavigate();
  
  // Use useMemo for performance optimization
  const filteredItems = useMemo(() => {
    // Mock data for restaurant names and images
    const mockMenuItems = [
      {
        _id: "1",
        name: "Classic Cheeseburger",
        category: "Burger",
        price: 9.99,
        ratings: 4.8,
        reviews: 245,
        description: "A juicy beef patty topped with melted cheddar, fresh lettuce, tomato, and a special sauce on a toasted bun.",
        image: "https://images.unsplash.com/photo-1571091718767-18b5b1457add?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        restaurantName: "Burger King",
        restaurantImage: "https://upload.wikimedia.org/wikipedia/commons/thumb/c/c2/Burger_King_logo_%281999%29.svg/2048px-Burger_King_logo_%281999%29.svg.png"
      },
      {
        _id: "2",
        name: "Pepperoni Pizza",
        category: "Pizza",
        price: 15.50,
        ratings: 4.5,
        reviews: 198,
        description: "A classic pizza topped with zesty tomato sauce, mozzarella cheese, and generous slices of savory pepperoni.",
        image: "https://images.unsplash.com/photo-1593560704563-f1a6f1d2d0b5?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        restaurantName: "Domino's",
        restaurantImage: "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3e/Domino%27s_pizza_logo.svg/2048px-Domino%27s_pizza_logo.svg.png"
      },
      {
        _id: "3",
        name: "Chocolate Lava Cake",
        category: "Dessert",
        price: 8.00,
        ratings: 4.9,
        reviews: 312,
        description: "A rich, moist chocolate cake with a molten center, served warm with a scoop of vanilla ice cream.",
        image: "https://images.unsplash.com/photo-1596700810769-1c97a87756f4?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        restaurantName: "The Cheesecake Factory",
        restaurantImage: "https://logos-world.net/wp-content/uploads/2020/12/The-Cheesecake-Factory-Logo.png"
      },
      {
        _id: "4",
