import React, { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { toast } from "react-toastify";

const Header = ({ user, onLogout, isAdmin, searchQuery, setSearchQuery, menuItems }) => {
  const navigate = useNavigate();
  const [showResults, setShowResults] = useState(false);
  const searchBarRef = useRef(null);

  const handleSignOut = () => {
    onLogout();
    toast.info("You have been signed out.");
    navigate("/auth");
  };

  // Filter menu items based on search query
  const filteredItems = menuItems.filter(item =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Handle click outside to close search results
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchBarRef.current && !searchBarRef.current.contains(event.target)) {
        setShowResults(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Show search results when query is not empty and input is focused
  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
    setShowResults(e.target.value.length > 0);
  };

  return (
    <header className="header p-5 md:px-20 py-5 flex items-center justify-between bg-white shadow-md sticky top-0 z-50">
      <Link to="/" className="logo text-3xl font-extrabold text-orange-600">
        SwadSeva
      </Link>

      <nav className="navbar flex items-center space-x-6">
        <Link
          to="/about"
          className="text-gray-600 hover:text-orange-600 transition-colors"
        >
          {isAdmin ? "Customer Orders" : "About"}
        </Link>
        <Link
          to="/menu"
          className="text-gray-600 hover:text-orange-600 transition-colors"
        >
          Menu
        </Link>
        {!isAdmin && (
          <Link
            to="/orders"
            className="text-gray-600 hover:text-orange-600 transition-colors"
          >
            My Orders
          </Link>
        )}
        <Link
          to="/contact"
          className="text-gray-600 hover:text-orange-600 transition-colors"
        >
          Contact
        </Link>
      </nav>

      <div className="flex items-center space-x-4">
        <div className="relative" ref={searchBarRef}>
          <input
            type="text"
            placeholder="Search food..."
            className="px-4 py-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-orange-500 transition-all duration-300 w-40 md:w-auto"
            value={searchQuery}
            onChange={handleInputChange}
            onFocus={() => searchQuery.length > 0 && setShowResults(true)}
          />
          {showResults && (
            <div className="absolute top-12 left-0 w-full bg-white border border-gray-200 rounded-lg shadow-lg max-h-60 overflow-y-auto z-50">
              {filteredItems.length > 0 ? (
                filteredItems.map((item) => (
                  <Link
                    key={item._id}
                    to="/menu"
                    className="flex items-center p-2 hover:bg-gray-100 transition-colors"
                    onClick={() => {
                      setSearchQuery(item.name);
                      setShowResults(false);
                    }}
                  >
                    <img
                      src={item.image}
                      alt={item.name}
                      className="w-10 h-10 object-cover rounded mr-2"
                    />
                    <div>
                      <p className="font-medium text-gray-800">{item.name}</p>
                      <p className="text-sm text-gray-500">${Number(item.price).toFixed(2)}</p>
                    </div>
                  </Link>
                ))
              ) : (
                <div className="p-4 text-center text-gray-500">No results found</div>
              )}
            </div>
          )}
        </div>

        {!isAdmin && (
          <Link
            to="/cart"
            className="relative text-gray-600 hover:text-orange-600 transition-colors"
          >
            <i className="fas fa-shopping-cart text-xl"></i>
          </Link>
        )}

        {user ? (
          <>
            <motion.span
              initial={{ opacity: 0, x: -10 }}
              animate={{ opacity: 1, x: 0 }}
              className="text-gray-800 font-medium whitespace-nowrap"
            >
              Welcome, {user.username}!
            </motion.span>
            <button
              onClick={handleSignOut}
              className="text-white bg-orange-600 hover:bg-orange-700 font-medium py-2 px-4 rounded-full transition-colors"
            >
              Sign Out
            </button>
          </>
        ) : (
          <Link
            to="/auth"
            className="text-white bg-orange-600 hover:bg-orange-700 font-medium py-2 px-4 rounded-full transition-colors"
          >
            Sign In
          </Link>
        )}
      </div>
    </header>
  );
};

export default Header;
