import React, { useEffect, useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircleCheck } from "@fortawesome/free-regular-svg-icons";
import {
  faUtensils,
  faHourglassHalf,
  faTruckFast,
  faCheckCircle,
} from "@fortawesome/free-solid-svg-icons";

export default function Contact({ isAdmin }) {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  // Function to determine the status of an order
  const getOrderStatus = (createdAt) => {
    const orderTime = new Date(createdAt).getTime();
    const currentTime = new Date().getTime();
    const elapsedTime = (currentTime - orderTime) / 1000 / 60; // elapsed time in minutes

    if (elapsedTime < 3) {
      return {
        status: "In Preparation",
        icon: faUtensils,
        color: "text-blue-500",
        steps: [true, false, false, false],
      };
    } else if (elapsedTime < 6) {
      return {
        status: "Out for Delivery",
        icon: faTruckFast,
        color: "text-orange-500",
        steps: [true, true, false, false],
      };
    } else if (elapsedTime < 10) {
      return {
        status: "Delivered",
        icon: faCheckCircle,
        color: "text-green-500",
        steps: [true, true, true, false],
      };
    } else {
      return {
        status: "Delivered",
        icon: faCheckCircle,
        color: "text-green-500",
        steps: [true, true, true, true],
      };
    }
  };

  const statusMap = ["Order Placed", "In Preparation", "Out for Delivery", "Delivered"];

  const getTimelineStatus = (createdAt) => {
    const orderTime = new Date(createdAt).getTime();
    const currentTime = new Date().getTime();
    const elapsedTime = (currentTime - orderTime) / 1000 / 60; // elapsed time in minutes

    let statusIndex = 0;
    if (elapsedTime >= 3) statusIndex = 1;
    if (elapsedTime >= 6) statusIndex = 2;
    if (elapsedTime >= 10) statusIndex = 3;
    return statusIndex;
  };

  const formatOrderTime = (createdAt) => {
    const date = new Date(createdAt);
    return date.toLocaleString();
  };

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/orders");
        setOrders(res.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();

    // Set an interval to update the status every 10 seconds
    const intervalId = setInterval(() => {
      setOrders((prevOrders) => [...prevOrders]);
    }, 10000);

    return () => clearInterval(intervalId);
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-[calc(100vh-150px)]">
        <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-orange-500"></div>
      </div>
    );
  }

  return (
    <div>
      <section
        id="contact"
        className="contact-section max-w-[1000px] mx-auto p-8 text-center"
      >
        <h2 className="text-[2rem] font-bold mb-6">
          Please Get In <span className="brand text-[#e96610]">Touch With Us</span>
        </h2>

        <div className="contact-info flex flex-wrap justify-center gap-6 mb-8">
          <div className="info-card bg-white rounded-[12px] p-6 max-w-[220px] shadow-[0_4px_15px_rgba(0,0,0,0.08)] hover:-translate-y-1 transition">
            <span className="text-2xl block mb-2">📍</span>
            <h3 className="text-lg font-semibold mb-1">Address</h3>
            <p className="text-[#666]">A108 Adam Street, New York, NY 535022</p>
          </div>
          <div className="info-card bg-white rounded-[12px] p-6 max-w-[220px] shadow-[0_4px_15px_rgba(0,0,0,0.08)] hover:-translate-y-1 transition">
            <span className="text-2xl block mb-2">📞</span>
            <h3 className="text-lg font-semibold mb-1">Call Us</h3>
            <p className="text-[#666]">+1 5589 55488 55</p>
          </div>
          <div className="info-card bg-white rounded-[12px] p-6 max-w-[220px] shadow-[0_4px_15px_rgba(0,0,0,0.08)] hover:-translate-y-1 transition">
            <span className="text-2xl block mb-2">📧</span>
            <h3 className="text-lg font-semibold mb-1">Email Us</h3>
            <p className="text-[#666]">info@example.com</p>
          </div>
          <div className="info-card bg-white rounded-[12px] p-6 max-w-[220px] shadow-[0_4px_15px_rgba(0,0,0,0.08)] hover:-translate-y-1 transition">
            <span className="text-2xl block mb-2">⏰</span>
            <h3 className="text-lg font-semibold mb-1">Open Hours</h3>
            <p className="text-[#666]">Mon-Sat: 11AM - 11PM</p>
          </div>
        </div>

        <form className="contact-form grid md:grid-cols-2 gap-6 bg-white p-8 rounded-[12px] shadow-[0_4px_15px_rgba(0,0,0,0.08)]">
          <input
            type="text"
            placeholder="Name"
            className="px-4 py-3 border border-[#e96610] rounded"
          />
          <input
            type="text"
            placeholder="Phone"
            className="px-4 py-3 border border-[#e96610] rounded"
          />
          <input
            type="email"
            placeholder="Email"
            className="px-4 py-3 border border-[#e96610] rounded md:col-span-2"
          />
          <input
            type="text"
            placeholder="Address"
            className="px-4 py-3 border border-[#e96610] rounded md:col-span-2"
          />
          <input
            type="text"
            placeholder="City"
            className="px-4 py-3 border border-[#e96610] rounded"
          />
          <input
            type="text"
            placeholder="State"
            className="px-4 py-3 border border-[#e96610] rounded"
          />

          <div className="md:col-span-2 flex justify-center mt-4">
            <button
              type="submit"
              aria-label="Submit"
              className="relative group inline-flex items-center justify-center bg-orange-600 hover:bg-orange-700 text-white px-6 py-2.5 rounded-md font-semibold shadow-sm transition-colors w-40"
            >
              {/* Submit Label */}
              <span className="transition-transform duration-300 group-hover:-translate-x-2">
                Submit
              </span>

              {/* Tick Icon */}
              <span className="absolute right-4 flex items-center opacity-0 transition-opacity duration-300 group-hover:opacity-100">
                <FontAwesomeIcon
                  icon={faCircleCheck}
                  className="text-white text-lg"
                />
              </span>
            </button>
          </div>
        </form>
      </section>

      {/* Track Orders Section */}
      <div className="max-w-[1200px] mx-auto p-4 md:p-8 min-h-screen">
        <h2 className="text-3xl md:text-4xl font-bold text-center text-gray-800 mb-8">
          Track Your Orders
        </h2>
        {orders.length === 0 ? (
          <p className="text-center text-gray-500">No orders to display.</p>
        ) : (
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {orders.map((order) => {
              const currentStatus = getOrderStatus(order.createdAt);
              const totalMinutes = 10;
              const createdAt = new Date(order.createdAt);
              const estimatedDeliveryTime = new Date(createdAt.getTime() + totalMinutes * 60000);
              const isDelivered = currentStatus.status === "Delivered";
              return (
                <motion.div
                  key={order._id}
                  className="bg-white p-6 rounded-lg shadow-lg border border-gray-200"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5 }}
                >
                  <div className="flex justify-between items-center mb-4 pb-4 border-b">
                    <div>
                      <h3 className="text-lg font-semibold text-gray-800">
                        Order ID: {order._id.slice(0, 8)}...
                      </h3>
                      <p className="text-sm text-gray-500">
                        Order Time: {formatOrderTime(order.createdAt)}
                      </p>
                      <p className="text-sm text-gray-500 mt-1">
                        Estimated Delivery: {estimatedDeliveryTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </p>
                    </div>
                    <div className="text-right">
                      <p className="font-bold text-gray-800 text-lg">
                        ${Number(order.totalPrice).toFixed(2)}
                      </p>
                      <p className="text-xs text-gray-500">Total Price</p>
                    </div>
                  </div>

                  {/* Delivery Timeline */}
                  <div className="mt-4">
                    <h4 className="font-semibold text-gray-700 mb-2">Delivery Timeline</h4>
                    <div className="relative">
                      {/* The line is a separate element for better positioning */}
                      <div className="absolute top-[6px] left-0 right-0 h-1 bg-gray-200 rounded-full" />
                      <div className="flex justify-between relative z-10">
                        {statusMap.map((status, index) => (
                          <div key={index} className="flex flex-col items-center z-10 w-1/4">
                            <div
                              className={`w-4 h-4 rounded-full transition-colors duration-500 ${
                                getTimelineStatus(order.createdAt) >= index
                                  ? "bg-orange-500"
                                  : "bg-gray-400"
                              }`}
                            />
                            <span className="text-center mt-2 text-xs font-medium text-gray-500 whitespace-nowrap">
                              {status}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>

                  <div className="mt-6">
                    <h4 className="font-semibold text-gray-700 mb-2">Order Details</h4>
                    <div className="flex flex-col gap-3">
                      {order.items.map((item) => (
                        <div
                          key={item._id}
                          className="flex items-center gap-4 bg-gray-50 p-3 rounded-lg"
                        >
                          <img
                            src={item.image}
                            alt={item.name}
                            className="w-16 h-16 object-cover rounded-lg"
                          />
                          <div className="flex-1">
                            <p className="font-semibold text-gray-800">
                              {item.name}{" "}
                              <span className="text-gray-500">x{item.quantity}</span>
                            </p>
                            <p className="text-sm text-gray-600">
                              ${Number(item.price).toFixed(2)} each
                            </p>
                          </div>
                          <p className="font-bold text-gray-800">
                            ${(item.price * item.quantity).toFixed(2)}
                          </p>
                        </div>
                      ))}
                    </div>
                    <div className="mt-6 pt-4 border-t flex justify-between items-center">
                      <p className="text-lg font-bold">
                        Total:{" "}
                        <span className="text-orange-500">
                          ${Number(order.totalPrice).toFixed(2)}
                        </span>
                      </p>
                      <motion.span
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ duration: 0.5 }}
                        className={`py-1 px-3 rounded-full text-sm font-medium ${
                          isDelivered ? "bg-green-100 text-green-700" : "bg-yellow-100 text-yellow-700"
                        }`}
                      >
                        {currentStatus.status}
                      </motion.span>
                    </div>
                  </div>
                </motion.div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}
