import React, { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { toast } from "react-toastify";

const Header = ({ user, onLogout, isAdmin, searchQuery, setSearchQuery, menuItems }) => {
  const navigate = useNavigate();
  const [showResults, setShowResults] = useState(false);
  const searchBarRef = useRef(null);

  const handleSignOut = () => {
    onLogout();
    toast.info("You have been signed out.");
    navigate("/auth");
  };

  // Filter menu items based on search query
  const filteredItems = menuItems.filter(item =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Handle click outside to close search results
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchBarRef.current && !searchBarRef.current.contains(event.target)) {
        setShowResults(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Show search results when query is not empty and input is focused
  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
    setShowResults(e.target.value.length > 0);
  };

  // Define the navigation links
  const navLinks = [
    { name: "Home", path: "/" },
    { name: "About", path: "/about" },
    { name: "Menu", path: "/menu" },
    { name: "Orders", path: "/orders" },
    // Conditional link based on isAdmin. The path is always /contact, but the text changes.
    { name: isAdmin ? "Track Order" : "Contact", path: "/contact" },
  ];

  return (
    <motion.header
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ type: "spring", stiffness: 120 }}
      className="bg-white sticky top-0 left-0 right-0 z-50 shadow-md py-4 px-8 flex justify-between items-center transition-all duration-300"
    >
      <Link to="/" className="flex items-center gap-2">
        <span className="text-2xl font-bold text-orange-600">FlavorFleet</span>
      </Link>

      <nav className="hidden md:flex gap-8">
        {navLinks.map((link) => (
          <Link
            key={link.name}
            to={link.path}
            className="text-gray-600 hover:text-orange-600 transition-colors relative group"
          >
            {link.name}
            <span className="absolute bottom-[-5px] left-0 w-full h-[2px] bg-orange-600 transform scale-x-0 group-hover:scale-x-100 transition-transform origin-left"></span>
          </Link>
        ))}
      </nav>

      <div className="flex items-center gap-4">
        <div className="relative" ref={searchBarRef}>
          <input
            type="text"
            placeholder="Search..."
            value={searchQuery}
            onChange={handleInputChange}
            onFocus={() => setShowResults(searchQuery.length > 0)}
            className="w-48 px-4 py-2 text-sm rounded-full bg-gray-100 focus:outline-none focus:ring-2 focus:ring-orange-500 transition-all"
          />
          {showResults && (
            <div className="absolute top-12 left-0 w-full bg-white border border-gray-200 rounded-lg shadow-lg max-h-60 overflow-y-auto">
              {filteredItems.length > 0 ? (
                filteredItems.map(item => (
                  <Link
                    to={`/menu#${item._id}`}
                    key={item._id}
                    onClick={() => {
                      setSearchQuery("");
                      setShowResults(false);
                    }}
                    className="flex items-center gap-4 p-3 hover:bg-gray-50 transition-colors"
                  >
                    <img
                      src={item.image}
                      alt={item.name}
                      className="w-10 h-10 object-cover rounded"
                    />
                    <div>
                      <p className="font-medium">{item.name}</p>
                      <p className="text-sm text-gray-500">${Number(item.price).toFixed(2)}</p>
                    </div>
                  </Link>
                ))
              ) : (
                <div className="p-4 text-center text-gray-500">No results found</div>
              )}
            </div>
          )}
        </div>

        {!isAdmin && (
          <Link
            to="/cart"
            className="relative text-gray-600 hover:text-orange-600 transition-colors"
          >
            <i className="fas fa-shopping-cart text-xl"></i>
          </Link>
        )}

        {user ? (
          <>
            <motion.span
              initial={{ opacity: 0, x: -10 }}
              animate={{ opacity: 1, x: 0 }}
              className="text-gray-800 font-medium whitespace-nowrap"
            >
              Welcome, {user.username}!
            </motion.span>
            <button
              onClick={handleSignOut}
              className="text-white bg-orange-600 hover:bg-orange-700 font-medium py-2 px-4 rounded-full transition-colors"
            >
              Sign Out
            </button>
          </>
        ) : (
          <Link
            to="/auth"
            className="text-white bg-orange-600 hover:bg-orange-700 font-medium py-2 px-4 rounded-full transition-colors"
          >
            Sign In
          </Link>
        )}
      </div>
    </motion.header>
  );
};

export default Header;
