import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { toast } from "react-toastify";

const Header = ({ user, onLogout, isAdmin, searchQuery, setSearchQuery }) => {
  const navigate = useNavigate();

  const handleSignOut = () => {
    localStorage.removeItem("userInfo");
    onLogout();
    toast.info("You have been signed out.");
    navigate("/auth");
  };

  return (
    <header className="sticky top-0 z-50 bg-white shadow-md">
      <nav className="flex items-center justify-between p-4 md:p-6 lg:p-8 max-w-[1200px] mx-auto">
        {/* LOGO */}
        <Link to="/" className="text-xl md:text-2xl font-bold text-orange-600">
          Food App
        </Link>

        {/* NAVIGATION LINKS */}
        <ul className="flex-grow justify-center gap-6 md:gap-10 lg:gap-16 hidden md:flex">
          <li>
            <Link to="/" className="text-gray-600 hover:text-orange-600 transition-colors font-medium">
              Home
            </Link>
          </li>
          <li>
            <Link to="/about" className="text-gray-600 hover:text-orange-600 transition-colors font-medium">
              About
            </Link>
          </li>
          <li>
            <Link to="/menu" className="text-gray-600 hover:text-orange-600 transition-colors font-medium">
              Menu
            </Link>
          </li>
          <li>
            <Link to="/orders" className="text-gray-600 hover:text-orange-600 transition-colors font-medium">
              {isAdmin ? "All Orders" : "My Orders"}
            </Link>
          </li>
          <li>
            <Link to="/contact" className="text-gray-600 hover:text-orange-600 transition-colors font-medium">
              Contact
            </Link>
          </li>
          {isAdmin && (
            <li>
              <Link to="/admin/create-menu" className="text-gray-600 hover:text-orange-600 transition-colors font-medium">
                Create Menu
              </Link>
            </li>
          )}
        </ul>

        {/* SEARCH BAR, CART & AUTH BUTTONS */}
        <div className="flex items-center space-x-4">
          {/* SEARCH BAR - Using a controlled input and react-router for navigation */}
          <div className="relative flex items-center bg-white rounded-full">
            <i className="fas fa-search absolute left-3 text-gray-400"></i>
            <input
              type="text"
              placeholder="Search food..."
              value={searchQuery}
              onChange={(e) => {
                setSearchQuery(e.target.value);
                navigate("/menu"); // Navigate to menu page on search
              }}
              className="pl-10 pr-4 py-2 rounded-full border border-gray-300 focus:outline-none focus:ring-2 focus:ring-orange-500 w-40 md:w-auto"
            />
          </div>

          {/* Cart Icon - Only visible to non-admins */}
          {!isAdmin && (
            <Link to="/cart" className="relative text-gray-600 hover:text-orange-600 transition-colors">
              <i className="fas fa-shopping-cart text-xl"></i>
            </Link>
          )}

          {/* User Status */}
          {user ? (
            <>
              <motion.span
                initial={{ opacity: 0, x: -10 }}
                animate={{ opacity: 1, x: 0 }}
                className="text-gray-800 font-medium whitespace-nowrap hidden sm:block"
              >
                Welcome, {user.username}!
              </motion.span>
              <button
                onClick={handleSignOut}
                className="text-white bg-orange-600 hover:bg-orange-700 font-medium py-2 px-4 rounded-full transition-colors"
              >
                Sign Out
              </button>
            </>
          ) : (
            <Link
              to="/auth"
              className="text-white bg-orange-600 hover:bg-orange-700 font-medium py-2 px-4 rounded-full transition-colors"
            >
              Sign In
            </Link>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
